export default function EjercicioEstrella() {
  const [favoritos, setFavoritos] = useState([]); 
  const [search, setSearch] = useState(""); 

  //  agregar o quitar favoritos
  const toggleFavorito = (id, comida) => {
    if (favoritos.includes(id)) {
      setFavoritos(favoritos.filter((f) => f !== id));
      Toast.show({
        type: "info",
        text1: `${comida} eliminado de favoritos`,
      });
    } else {
      setFavoritos([...favoritos, id]);
      Toast.show({
        type: "success",
        text1: `${comida} agregado a favoritos ‚ù§Ô∏è`,
      });
    }
  };

// filtrar recetas
  const filtrado = recetas.map((section) => ({
    ...section,
    data: section.data.filter((item) =>
      item.comida.toLowerCase().includes(search.toLowerCase())
    ),
  }));

  return (
    <SafeAreaProvider>
      <SafeAreaView style={styles.container}>
        <View style={styles.header}>
          <Text style={styles.headerText}>üç≤ Recetas Caseras</Text>
          <Text style={styles.counter}>Favoritos: {favoritos.length}</Text>
        </View>

        <TextInput
          style={styles.input}
          placeholder="Buscar receta..."
          value={search}
          onChangeText={setSearch}
        />

        {/* Lista con SectionList */}
        <SectionList
          sections={filtrado}
          keyExtractor={(item) => item.id}
          renderItem={({ item }) => (
            <View style={styles.card}>
              <Image source={{ uri: item.imagenPersona }} style={styles.avatar} />
              <Image source={{ uri: item.img }} style={styles.foodImage} />
              <Text style={styles.comida}>{item.comida}</Text>
              <Text style={styles.persona}>Receta hecha por {item.nombrePersona}</Text>
              <Text style={styles.descripcion}>{item.descripcion}</Text>

              {/* Bot√≥n de favoritos */}
              <Pressable
                style={styles.boton}
                onPress={() => toggleFavorito(item.id, item.comida)}
              >
                <Ionicons
                  name={
                    favoritos.includes(item.id)
                      ? "heart"
                      : "heart-outline"
                  }
                  size={20}
                  color="red"
                />
                <Text style={styles.botonTexto}>
                  {favoritos.includes(item.id)
                    ? " Quitar favorito"
                    : " Agregar favorito"}
                </Text>
              </Pressable>
            </View>
          )}
          renderSectionHeader={({ section: { title } }) => (
            <Text style={styles.sectionHeader}>{title}</Text>
          )}
        />

        {/* Toast */}
        <Toast />
      </SafeAreaView>
    </SafeAreaProvider>
  );
}


